@import url("https://fonts.googleapis.com/css?family=Roboto+Condensed");
body {
  font-family: 'Roboto Condensed', sans-serif;
  margin: 0;
  background-color: #ddd;
  height: auto; }

h1 {
  font-weight: 400;
  font-size: 2.5rem;
  text-transform: uppercase;
  margin: 0; }

h2 {
  font-weight: 400;
  font-size: 1.2rem;
  text-transform: capitalize;
  margin: 0; }

img {
  display: block;
  width: 100%; }

main {
  max-width: 900px;
  margin: auto;
  box-shadow: 30px 0px 40px rgba(0, 0, 0, 0.1), -30px 0px 40px rgba(0, 0, 0, 0.1); }
  main #landing {
    background: #fff; }
    main #landing #landing-text {
      display: flex;
      flex: 0 1 40vw;
      height: 50vh;
      justify-content: center;
      align-items: center;
      text-align: center;
      padding-right: 1rem;
      padding-left: 1rem; }
      main #landing #landing-text h2 {
        color: #888; }
      main #landing #landing-text .btn {
        padding: 0.5rem 2rem;
        border: 1px solid #ccc;
        display: inline-block;
        margin: 2rem 0 0;
        border-radius: 50px;
        text-decoration: none;
        color: #333;
        transition: background 500ms ease; }
      main #landing #landing-text .btn:hover {
        background: #f4f4f4; }
  main #landing-image {
    background: url("https://source.unsplash.com/De8wMYoSSBc");
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
    height: 50vh;
    flex: 0 1 60vw;
    margin: 0; }

#header {
  padding: 1.5rem;
  text-align: center;
  background: #333;
  color: #fff; }
  #header h2 {
    border-left: dotted 1px #fff;
    border-right: dotted 1px #fff;
    display: inline-block;
    padding: 0 1rem; }

.caption {
  padding: 0.8rem;
  text-align: center; }

footer {
  text-align: center;
  padding: 2rem 1rem;
  margin: auto;
  color: #333; }
  footer h3 {
    font-size: 3rem;
    margin-bottom: 0; }

@media (min-width: 500px) {
  #landing {
    display: flex;
    height: 100%; }
  #landing-text {
    height: 100vh; }
  #landing-image {
    height: 100vh; } }

@media (min-width: 700px) {
  .btn {
    padding: 1rem 3rem;
    color: red; } }
